import random

def game():
    number = random.randint(1, 100)
    attempts = 0

    print("Вгадайте число від 1 до 100")

    while True:
        guess = int(input("Введіть ваше число: "))
        attempts += 1

        if guess < number:
            print("Загадане число більше")
        elif guess > number:
            print("Загадане число менше")
        else:
            print(f"Вітаю! Ви вгадали число за {attempts} спроб")
            break

if __name__ == "__main__":
    game()
